mapper.select:
SELECT bc.* from buscenRole br,buscenter bc WHERE br.buscenId=bc.buscenId and br.roleId in

    #{item}

GROUP BY buscenId

mapper.update:
update buscenter
buscenStatus=#{buscenStatus}, parentId=#{parentId}, buscenName=#{title}, buscenDistrict=#{buscenDistrict}, buscenManagerId=#{buscenManagerId}, spread=#{spread},
where buscenId=#{id}
