mapper.select:
select menu.menuId,parentid,title,icon,href,spread from menu,rolemenu where rolemenu.menuId=menu.menuId and roleId in

    #{item}

group by menu.menuId

mapper.update:
update menu
available=#{available}, parentId=#{parentId}, title=#{title}, icon=#{icon}, href=#{href}, spread=#{spread},
where menuId=#{menuId}
