mapper.update:
update bus

    
        busName=#{busName},
    
    
        busDriverId=#{busDriverId},
    
    
        busLineId=#{busLineId},
    
    
        busStatus=#{busStatus}
    

where busId=#{busId}

mapper.select:
select a.busId, a.busPlate, a.busCreatetime, a.busStatus, a.busName, b.busdriverId, b.busdriverName, c.lineId, c.lineName FROM bus a left JOIN busdriver b on a.busDriverid = b.busdriverId left JOIN busline c on a.busLineid = c.lineId where a.busStatus != 4 and a.busId in
#{id}
order by a.busId desc
